import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Editor from '@monaco-editor/react';
import { Terminal } from 'xterm';
import { FitAddon } from 'xterm-addon-fit';
import 'xterm/css/xterm.css';
import styled from 'styled-components';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: #1e1e1e;
  color: white;
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 5px 10px;
  background-color: #252526;
  border-bottom: 1px solid #333;
`;

const LeftControls = styled.div`
  display: flex;
  align-items: center;
`;

const RightControls = styled.div`
  display: flex;
  align-items: center;
`;

const TabBar = styled.div`
  display: flex;
  background-color: #2d2d2d;
  border-bottom: 1px solid #333;
  padding-left: 10px;
`;

const Tab = styled.div`
  padding: 8px 15px;
  cursor: pointer;
  border-right: 1px solid #333;
  background-color: ${props => (props.active ? '#1e1e1e' : 'transparent')};
  color: ${props => (props.active ? 'white' : '#888')};
  &:hover {
    background-color: ${props => (props.active ? '#1e1e1e' : '#3c3c3c')};
  }
`;

const AddTabButton = styled.div`
  padding: 8px 10px;
  cursor: pointer;
  border-right: 1px solid #333;
  color: #888;
  &:hover {
    background-color: #3c3c3c;
  }
`;

const EditorContainer = styled.div`
  flex: 1;
  min-height: 50vh;
  position: relative;
  display: flex;
  flex-direction: column;
`;

const TerminalContainer = styled.div`
  height: 40vh;
  background-color: #000;
  padding: 10px;
  flex-shrink: 0;
  overflow-y: auto;
  font-family: 'Courier New', Courier, monospace;
  font-size: 14px;
  color: #eee;
  white-space: pre-wrap;
`;

const Button = styled.button`
  background-color: #4CAF50;
  color: white;
  padding: 5px 15px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin: 0 5px;
  font-size: 14px;
  flex-shrink: 0;
  display: flex;
  align-items: center;

  &:hover {
    background-color: #45a049;
  }
`;

const ToolbarIcon = styled.div`
  padding: 5px;
  cursor: pointer;
  color: #888;
  margin: 0 5px;

  &:hover {
    color: white;
  }
`;

const ControlsContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 10px;
  background-color: #252526;
`;

const CommandLineInput = styled.input`
  margin-left: 20px;
  padding: 5px 8px;
  border-radius: 4px;
  border: 1px solid #555;
  background-color: #333;
  color: white;
  font-size: 14px;
  flex-grow: 1;
  outline: none;

  &::placeholder {
    color: #888;
  }
`;

const defaultCode = `# Welcome to the Python Code Editor!
# Write your Python code here and click "Run Code"

print("Hello, World!")
`;

function App() {
  const [code, setCode] = useState(defaultCode);
  const [editorMounted, setEditorMounted] = useState(false);
  const [commandLineArgs, setCommandLineArgs] = useState('');
  const terminalRef = useRef(null);
  const wsRef = useRef(null);
  const terminalInstanceRef = useRef(null);
  const editorRef = useRef(null);
  const inputBufferRef = useRef('');
  const isProgramRunning = useRef(false);
  const editorContainerRef = useRef(null);
  const fitAddonRef = useRef(null);
  const isTerminalReadyForInput = useRef(false);

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    setEditorMounted(true);
  };

  useLayoutEffect(() => {
    if (!editorContainerRef.current) return;

    const terminal = new Terminal({
      cursorBlink: true,
      theme: {
        background: '#000000'
      },
      convertEol: true,
      cursorStyle: 'block',
      disableStdin: true // Disable built-in input handling
    });
    const fitAddon = new FitAddon();
    terminal.loadAddon(fitAddon);
    terminal.open(terminalRef.current);
    fitAddon.fit();
    terminalInstanceRef.current = terminal;
    fitAddonRef.current = fitAddon;

    terminal.onData((data) => {
      if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN && isProgramRunning.current && isTerminalReadyForInput.current) {
        if (data === '\r') { // Enter key
          const input = inputBufferRef.current + '\n';
          terminal.write('\r\n');
          wsRef.current.send(JSON.stringify({
            type: 'input',
            input: input
          }));
          inputBufferRef.current = '';
          isTerminalReadyForInput.current = false; // Wait for next prompt
        } else if (data === '\x7f') { // Backspace
          if (inputBufferRef.current.length > 0) {
            inputBufferRef.current = inputBufferRef.current.slice(0, -1);
            terminal.write('\b \b');
          }
        } else if (data >= ' ' && data <= '~') { // Printable characters
          inputBufferRef.current += data;
          terminal.write(data);
        }
      } else if (!isProgramRunning.current && data === '\r') {
           terminalInstanceRef.current.clear();
      }
    });

    wsRef.current = new WebSocket('ws://localhost:8000/ws/terminal');

    wsRef.current.onmessage = (event) => {
      const data = event.data;
      terminal.write(data);

      // Check if the message ends with a typical input prompt
      if (data.trim().endsWith(':') || data.trim().endsWith('>>> ') || data.trim().endsWith('...')) {
           isTerminalReadyForInput.current = true;
      } else if (data.includes('** Process exited')) {
         isProgramRunning.current = false;
         isTerminalReadyForInput.current = false; // No input after process exits
      }
    };

    wsRef.current.onerror = (error) => {
      terminal.write('\r\n\x1b[31mError: Could not connect to server\x1b[0m\r\n');
      isProgramRunning.current = false;
      isTerminalReadyForInput.current = false;
    };

    return () => {
      terminal.dispose();
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, [editorMounted]);

  useEffect(() => {
    const handleResize = () => {
      if (editorRef.current && editorContainerRef.current && editorContainerRef.current.offsetWidth > 0 && editorContainerRef.current.offsetHeight > 0) {
        requestAnimationFrame(() => {
           editorRef.current.layout();
        });
      }
      if (fitAddonRef.current && terminalRef.current) {
        requestAnimationFrame(() => {
           fitAddonRef.current.fit();
        });
      }
    };

    window.addEventListener('resize', handleResize);

    requestAnimationFrame(() => {
       if (editorRef.current && editorContainerRef.current && editorContainerRef.current.offsetWidth > 0 && editorContainerRef.current.offsetHeight > 0) {
         editorRef.current.layout();
       }
       if (fitAddonRef.current && terminalRef.current) {
         fitAddonRef.current.fit();
       }
    });

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [editorMounted]);

  const handleRunCode = () => {
    if (wsRef.current && wsRef.current.readyState === WebSocket.OPEN) {
      terminalInstanceRef.current.clear();
      inputBufferRef.current = '';
      isProgramRunning.current = true;
      isTerminalReadyForInput.current = false; // Reset input readiness at the start of a new run
      wsRef.current.send(JSON.stringify({
        type: 'execute',
        code: code,
        args: commandLineArgs.split(' ').filter(arg => arg.length > 0)
      }));
    }
  };

  return (
    <AppContainer>
      <Header>
        <LeftControls>
          <ToolbarIcon title="Open">📁</ToolbarIcon>
          <ToolbarIcon title="Save">💾</ToolbarIcon>
          <ToolbarIcon title="Undo">↩️</ToolbarIcon>
          <ToolbarIcon title="Redo">↪️</ToolbarIcon>
          <Button onClick={handleRunCode}>▶️ Run</Button>
        </LeftControls>
        <RightControls>
           <ToolbarIcon title="Toggle Theme">🌙</ToolbarIcon>
           <ToolbarIcon title="Info">ℹ️</ToolbarIcon>
           <ToolbarIcon title="Settings">⚙️</ToolbarIcon>
        </RightControls>
      </Header>
      <TabBar>
         <Tab active>main.py</Tab>
         <Tab>Untitled2.py</Tab>
         <AddTabButton title="Add Tab">+</AddTabButton>
      </TabBar>
      <EditorContainer ref={editorContainerRef}>
        <Editor
          height="100%"
          defaultLanguage="python"
          defaultValue={defaultCode}
          theme="vs-dark"
          onChange={(value) => setCode(value)}
          onMount={handleEditorDidMount}
          options={{
            minimap: { enabled: false },
            fontSize: 14,
            lineNumbers: 'on',
            roundedSelection: false,
            scrollBeyondLastLine: false,
            automaticLayout: false,
            wordWrap: 'on',
            fixedOverflowWidgets: true,
            renderWhitespace: 'none',
            folding: false,
            lineDecorationsWidth: 0,
            lineNumbersMinChars: 3,
            glyphMargin: false,
            foldingHighlight: false,
            renderLineHighlight: 'none',
            contextmenu: false,
            scrollbar: {
              vertical: 'visible',
              horizontal: 'visible',
              useShadows: false,
              verticalScrollbarSize: 10,
              horizontalScrollbarSize: 10
            }
          }}
        />
      </EditorContainer>
      <ControlsContainer>
        <Button title="Share">$ Share</Button>
        <CommandLineInput 
          type="text" 
          placeholder="Command Line Arguments" 
          value={commandLineArgs} 
          onChange={(e) => setCommandLineArgs(e.target.value)}
          title="Enter command line arguments here"
        />
      </ControlsContainer>
      <TerminalContainer ref={terminalRef} />
    </AppContainer>
  );
}

export default App;
