import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Editor from '@monaco-editor/react';
import { Terminal } from 'xterm';
import { FitAddon } from 'xterm-addon-fit';
import 'xterm/css/xterm.css';
import styled, { ThemeProvider } from 'styled-components';
import { FaFolder, FaFile, FaSave, FaUndo, FaRedo, FaCog, FaSun, FaMoon } from 'react-icons/fa';

// Theme configuration
const themes = {
  dark: {
    background: '#1e1e1e',
    foreground: '#ffffff',
    editorBg: '#1e1e1e',
    headerBg: '#252526',
    border: '#333333',
    tabBg: '#2d2d2d',
    tabActive: '#1e1e1e',
    tabInactive: 'transparent',
    tabHover: '#3c3c3c',
    buttonBg: '#4CAF50',
    buttonHover: '#45a049',
    iconColor: '#888888',
    iconHover: '#ffffff',
    inputBg: '#333333',
    inputBorder: '#555555'
  },
  light: {
    background: '#ffffff',
    foreground: '#000000',
    editorBg: '#ffffff',
    headerBg: '#f3f3f3',
    border: '#e0e0e0',
    tabBg: '#f8f8f8',
    tabActive: '#ffffff',
    tabInactive: 'transparent',
    tabHover: '#e8e8e8',
    buttonBg: '#4CAF50',
    buttonHover: '#45a049',
    iconColor: '#666666',
    iconHover: '#000000',
    inputBg: '#ffffff',
    inputBorder: '#cccccc'
  }
};

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: ${props => props.theme.background};
  color: ${props => props.theme.foreground};
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 5px 10px;
  background-color: ${props => props.theme.headerBg};
  border-bottom: 1px solid ${props => props.theme.border};
`;

const LeftControls = styled.div`
  display: flex;
  align-items: center;
`;

const RightControls = styled.div`
  display: flex;
  align-items: center;
`;

const TabBar = styled.div`
  display: flex;
  background-color: ${props => props.theme.tabBg};
  border-bottom: 1px solid ${props => props.theme.border};
  padding-left: 10px;
`;

const Tab = styled.div`
  padding: 8px 15px;
