const defaultCode = `# Welcome to the Python Code Editor!
# Write your Python code here and click "Run Code"

print("Hello, World!")
`;

function App() {
  const [code, setCode] = useState(defaultCode);
  const [editorMounted, setEditorMounted] = useState(false);
  const [commandLineArgs, setCommandLineArgs] = useState('');
  const [theme, setTheme] = useState('dark');
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [files, setFiles] = useState([]);
  const [currentFile, setCurrentFile] = useState(null);
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const terminalRef = useRef(null);
  const wsRef = useRef(null);
  const terminalInstanceRef = useRef(null);
  const editorRef = useRef(null);
  const inputBufferRef = useRef('');
  const isProgramRunning = useRef(false);
  const editorContainerRef = useRef(null);
  const fitAddonRef = useRef(null);

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    setEditorMounted(true);

    // Add undo/redo handlers
    editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyZ, () => {
      handleUndo();
    });

    editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.KeyZ, () => {
      handleRedo();
    });
  };

  const handleUndo = () => {
    if (undoStack.length > 0) {
      const previousState = undoStack[undoStack.length - 1];
      setRedoStack([...redoStack, code]);
      setCode(previousState);
      setUndoStack(undoStack.slice(0, -1));
    }
  };

  const handleRedo = () => {
    if (redoStack.length > 0) {
      const nextState = redoStack[redoStack.length - 1];
      setUndoStack([...undoStack, code]);
      setCode(nextState);
      setRedoStack(redoStack.slice(0, -1));
    }
  };

  const handleSave = () => {
    if (currentFile) {
      // Save to existing file
      const updatedFiles = files.map(f => 
        f.id === currentFile.id ? { ...f, content: code } : f
      );
      setFiles(updatedFiles);
    } else {
      // Create new file
      const newFile = {
        id: Date.now(),
        name: 'untitled.py',
        content: code
      };
      setFiles([...files, newFile]);
      setCurrentFile(newFile);
    }
  };

  const handleThemeToggle = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  const handleSettingsToggle = () => {
    setIsSettingsOpen(!isSettingsOpen);
  };

  useLayoutEffect(() => {
    if (!terminalRef.current) return;

    // Initialize terminal with proper configuration
    const terminal = new Terminal({
      cursorBlink: true,
      theme: { background: '#000000' },
      convertEol: true,
      cursorStyle: 'block',
      disableStdin: true,
      scrollback: 10000,
      fontSize: 14,
      fontFamily: 'Consolas, monospace',
      rendererType: 'canvas',
      allowTransparency: true,
      windowsMode: true,
      cols: 80,
      rows: 24
    });

    // Initialize and configure fit addon
    const fitAddon = new FitAddon();
    terminal.loadAddon(fitAddon);
    terminal.open(terminalRef.current);

    // Store refs
    fitAddonRef.current = fitAddon;
    terminalInstanceRef.current = terminal;

    // Initial fit
    requestAnimationFrame(() => {
      fitAddon.fit();
    });

    // Handle terminal input
    terminal.onData((data) => {
      if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
        terminal.write('\r\n\x1b[31mNot connected to server\x1b[0m\r\n');
        return;
      }

      if (!isProgramRunning.current) {
        if (data === '\r') {
          terminal.clear();
          terminal.write('\r\n');
        }
        return;
      }

      switch (data) {
        case '\r': // Enter
          const input = inputBufferRef.current + '\n';
          wsRef.current.send(JSON.stringify({
            type: 'input',
            input: input
          }));
          inputBufferRef.current = '';
          terminal.write('\r\n');
          break;

        case '\x7f': // Backspace
          if (inputBufferRef.current.length > 0) {
            inputBufferRef.current = inputBufferRef.current.slice(0, -1);
            terminal.write('\b \b');
          }
          break;

        default:
          if (data >= ' ' && data <= '~') {
            inputBufferRef.current += data;
            terminal.write(data);
          }
      }
    });

    // Initialize WebSocket
    wsRef.current = new WebSocket('ws://localhost:8000/ws/terminal');

    wsRef.current.onmessage = (event) => {
      try {
        const data = event.data;
        // Write data directly to terminal
        terminal.write(data);

        if (data.includes('** Process exited')) {
          isProgramRunning.current = false;
          inputBufferRef.current = '';
          terminal.write('\r\n');
        }
      } catch (error) {
        console.error('Error writing to terminal:', error);
        terminal.write('\r\n\x1b[31mError displaying output\x1b[0m\r\n');
      }
    };

    wsRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      terminal.write('\r\n\x1b[31mError: Could not connect to server\x1b[0m\r\n');
      isProgramRunning.current = false;
      inputBufferRef.current = '';
    };

    wsRef.current.onclose = () => {
      console.log('WebSocket connection closed');
      terminal.write('\r\n\x1b[31mConnection to server closed\x1b[0m\r\n');
      isProgramRunning.current = false;
      inputBufferRef.current = '';
    };

    return () => {
      terminal.dispose();
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      requestAnimationFrame(() => {
        if (fitAddonRef.current) {
          fitAddonRef.current.fit();
        }
        if (editorRef.current && editorContainerRef.current) {
          editorRef.current.layout();
        }
      });
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial layout

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [editorMounted]);

  const handleRunCode = () => {
    if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
      terminalInstanceRef.current.write('\r\n\x1b[31mError: Not connected to server\x1b[0m\r\n');
      return;
    }

    terminalInstanceRef.current.clear();
    inputBufferRef.current = '';
    isProgramRunning.current = true;
    terminalInstanceRef.current.write('\r\n');

    wsRef.current.send(JSON.stringify({
      type: 'execute',
      code: code,
      args: commandLineArgs.split(' ').filter(arg => arg.length > 0)
    }));
  };

  return (
    <ThemeProvider theme={themes[theme]}>
      <AppContainer>
        <Header>
          <LeftControls>
            <ToolbarIcon onClick={handleSave}>
              <FaSave />
            </ToolbarIcon>
            <ToolbarIcon onClick={handleUndo}>
              <FaUndo />
            </ToolbarIcon>
            <ToolbarIcon onClick={handleRedo}>
              <FaRedo />
            </ToolbarIcon>
          </LeftControls>
          <RightControls>
            <ToolbarIcon onClick={handleThemeToggle}>
              {theme === 'dark' ? <FaSun /> : <FaMoon />}
            </ToolbarIcon>
            <ToolbarIcon onClick={handleSettingsToggle}>
              <FaCog />
            </ToolbarIcon>
          </RightControls>
        </Header>

        <MainContent>
          <Sidebar>
            <FileTree>
              {files.map(file => (
                <FileItem
                  key={file.id}
                  onClick={() => {
                    setCurrentFile(file);
                    setCode(file.content);
                  }}
                >
                  <FaFile style={{ marginRight: '5px' }} />
                  {file.name}
                </FileItem>
              ))}
            </FileTree>
          </Sidebar>

          <EditorContainer ref={editorContainerRef}>
            <Editor
              height="100%"
              defaultLanguage="python"
              value={code}
              onChange={newValue => {
                setUndoStack([...undoStack, code]);
                setCode(newValue);
              }}
              theme={theme === 'dark' ? 'vs-dark' : 'light'}
              onMount={handleEditorDidMount}
              options={{
                minimap: { enabled: false },
                fontSize: 14,
                wordWrap: 'on',
                automaticLayout: true
              }}
            />
          </EditorContainer>

          <SettingsPanel isOpen={isSettingsOpen}>
            <h2>Settings</h2>
            <SettingsItem>
              <SettingsLabel>Font Size</SettingsLabel>
              <SettingsInput
                type="number"
                min="8"
                max="24"
                defaultValue="14"
                onChange={e => {
                  if (editorRef.current) {
                    editorRef.current.updateOptions({ fontSize: parseInt(e.target.value) });
                  }
                }}
              />
            </SettingsItem>
            <SettingsItem>
              <SettingsLabel>Tab Size</SettingsLabel>
              <SettingsInput
                type="number"
                min="2"
                max="8"
                defaultValue="4"
                onChange={e => {
                  if (editorRef.current) {
                    editorRef.current.updateOptions({ tabSize: parseInt(e.target.value) });
                  }
                }}
              />
            </SettingsItem>
          </SettingsPanel>
        </MainContent>

        <TerminalContainer ref={terminalRef} />
      </AppContainer>
    </ThemeProvider>
  );
}

export default App;
