import React, { useEffect, useRef, useState, useLayoutEffect } from 'react';
import Editor from '@monaco-editor/react';
import { Terminal } from 'xterm';
import { FitAddon } from 'xterm-addon-fit';
import 'xterm/css/xterm.css';
import styled, { ThemeProvider } from 'styled-components';
import { FaFolder, FaFile, FaFolderOpen, FaSave, FaUndo, FaRedo, FaCog, FaSun, FaMoon, FaPlay, FaPlus, FaChevronRight, FaChevronDown, FaTrash, FaEdit, FaChevronRight as FaChevronRightSmall } from 'react-icons/fa';

// Theme configuration
const themes = {
  dark: {
    background: '#1e1e1e',
    foreground: '#ffffff',
    editorBg: '#1e1e1e',
    headerBg: '#252526',
    border: '#333333',
    tabBg: '#2d2d2d',
    tabActive: '#1e1e1e',
    tabInactive: 'transparent',
    tabHover: '#3c3c3c',
    buttonBg: '#4CAF50',
    buttonHover: '#45a049',
    iconColor: '#888888',
    iconHover: '#ffffff',
    inputBg: '#333333',
    inputBorder: '#555555'
  },
  light: {
    background: '#ffffff',
    foreground: '#000000',
    editorBg: '#ffffff',
    headerBg: '#f3f3f3',
    border: '#e0e0e0',
    tabBg: '#f8f8f8',
    tabActive: '#ffffff',
    tabInactive: 'transparent',
    tabHover: '#e8e8e8',
    buttonBg: '#4CAF50',
    buttonHover: '#45a049',
    iconColor: '#666666',
    iconHover: '#000000',
    inputBg: '#ffffff',
    inputBorder: '#cccccc'
  }
};

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  height: 100vh;
  background-color: ${props => props.theme.background};
  color: ${props => props.theme.foreground};
  overflow: hidden;
`;

const Header = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 5px 10px;
  background-color: ${props => props.theme.headerBg};
  border-bottom: 1px solid ${props => props.theme.border};
`;

const LeftControls = styled.div`
  display: flex;
  align-items: center;
`;

const RightControls = styled.div`
  display: flex;
  align-items: center;
`;

const TabBar = styled.div`
  display: flex;
  background-color: ${props => props.theme.tabBg};
  border-bottom: 1px solid ${props => props.theme.border};
  padding-left: 10px;
`;

const Tab = styled.div`
  padding: 8px 15px;
  cursor: pointer;
  border-right: 1px solid ${props => props.theme.border};
  background-color: ${props => (props.active ? props.theme.tabActive : props.theme.tabInactive)};
  color: ${props => (props.active ? 'white' : props.theme.iconColor)};
  &:hover {
    background-color: ${props => (props.active ? props.theme.tabHover : props.theme.tabHover)};
  }
`;

const AddTabButton = styled.div`
  padding: 8px 10px;
  cursor: pointer;
  border-right: 1px solid ${props => props.theme.border};
  color: ${props => props.theme.iconColor};
  &:hover {
    background-color: ${props => props.theme.tabHover};
  }
`;

const EditorWrapper = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 0;
  position: relative;
`;

const EditorContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  min-height: 0;
  position: relative;
  overflow: hidden;
`;

const TerminalContainer = styled.div`
  height: 40vh;
  background-color: #000;
  padding: 10px;
  flex-shrink: 0;
  overflow-y: auto;
`;

const Button = styled.button`
  background-color: ${props => props.theme.buttonBg};
  color: white;
  padding: 5px 15px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  margin: 0 5px;
  font-size: 14px;
  flex-shrink: 0;
  display: flex;
  align-items: center;

  &:hover {
    background-color: ${props => props.theme.buttonHover};
  }
`;

const ToolbarIcon = styled.div`
  padding: 5px;
  cursor: pointer;
  color: ${props => props.theme.iconColor};
  margin: 0 5px;

  &:hover {
    color: ${props => props.theme.iconHover};
  }
`;

const ControlsContainer = styled.div`
  display: flex;
  align-items: center;
  padding: 10px;
  background-color: ${props => props.theme.headerBg};
`;

const CommandLineInput = styled.input`
  margin-left: 20px;
  padding: 5px 8px;
  border-radius: 4px;
  border: 1px solid ${props => props.theme.inputBorder};
  background-color: ${props => props.theme.inputBg};
  color: ${props => props.theme.foreground};
  font-size: 14px;
  flex-grow: 1;
  outline: none;

  &::placeholder {
    color: ${props => props.theme.iconColor};
  }
`;

const Sidebar = styled.div`
  width: 250px;
  background-color: ${props => props.theme.headerBg};
  border-right: 1px solid ${props => props.theme.border};
  display: flex;
  flex-direction: column;
`;

const FileTree = styled.div`
  flex: 1;
  overflow-y: auto;
  padding: 10px;
`;

const FileLocation = styled.div`
  display: flex;
  align-items: center;
  padding: 5px 10px;
  background-color: ${props => props.theme.headerBg};
  border-bottom: 1px solid ${props => props.theme.border};
  color: ${props => props.theme.foreground};
  font-size: 12px;
`;

const LocationItem = styled.div`
  display: flex;
  align-items: center;
  color: ${props => props.theme.iconColor};

  &:hover {
    color: ${props => props.theme.iconHover};
  }
`;

const LocationSeparator = styled.div`
  margin: 0 5px;
  color: ${props => props.theme.iconColor};
`;

const FileTreeItem = styled.div`
  display: flex;
  align-items: center;
  padding: 5px;
  cursor: pointer;
  color: ${props => props.theme.foreground};
  padding-left: ${props => props.depth * 20}px;

  &:hover {
    background-color: ${props => props.theme.tabHover};
  }
`;

const FileTreeContent = styled.div`
  display: flex;
  align-items: center;
  flex: 1;
`;

const FileTreeIcon = styled.div`
  margin-right: 5px;
  display: flex;
  align-items: center;
  width: 16px;
`;

const FileTreeName = styled.span`
  flex: 1;
`;

const FileTreeActions = styled.div`
  display: flex;
  align-items: center;
  opacity: 0;
  transition: opacity 0.2s;

  ${FileTreeItem}:hover & {
    opacity: 1;
  }
`;

const FileTreeAction = styled.div`
  padding: 2px;
  margin-left: 4px;
  cursor: pointer;
  color: ${props => props.theme.iconColor};

  &:hover {
    color: ${props => props.theme.iconHover};
  }
`;

const SettingsPanel = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  width: 300px;
  height: 100%;
  background-color: ${props => props.theme.headerBg};
  border-left: 1px solid ${props => props.theme.border};
  padding: 20px;
  transform: translateX(${props => props.isOpen ? '0' : '100%'});
  transition: transform 0.3s ease;
  z-index: 1000;
`;

const SettingsItem = styled.div`
  margin-bottom: 15px;
`;

const SettingsLabel = styled.label`
  display: block;
  margin-bottom: 5px;
  color: ${props => props.theme.foreground};
`;

const SettingsInput = styled.input`
  width: 100%;
  padding: 5px;
  background-color: ${props => props.theme.inputBg};
  border: 1px solid ${props => props.theme.inputBorder};
  color: ${props => props.theme.foreground};
  border-radius: 4px;
`;

const MainContent = styled.div`
  display: flex;
  flex: 1;
  overflow: hidden;
`;

const defaultCode = `# Welcome to the Python Code Editor!
# Write your Python code here and click "Run Code"

print("Hello, World!")
`;

function App() {
  const [code, setCode] = useState(defaultCode);
  const [editorMounted, setEditorMounted] = useState(false);
  const [commandLineArgs, setCommandLineArgs] = useState('');
  const [theme, setTheme] = useState('dark');
  const [isSettingsOpen, setIsSettingsOpen] = useState(false);
  const [files, setFiles] = useState([]);
  const [currentFile, setCurrentFile] = useState(null);
  const [undoStack, setUndoStack] = useState([]);
  const [redoStack, setRedoStack] = useState([]);
  const terminalRef = useRef(null);
  const wsRef = useRef(null);
  const terminalInstanceRef = useRef(null);
  const editorRef = useRef(null);
  const inputBufferRef = useRef('');
  const isProgramRunning = useRef(false);
  const editorContainerRef = useRef(null);
  const fitAddonRef = useRef(null);
  const [fileTree, setFileTree] = useState([
    {
      id: 'root',
      name: 'Project',
      type: 'folder',
      isOpen: true,
      children: [
        {
          id: '1',
          name: 'main.py',
          type: 'file',
          content: defaultCode
        }
      ]
    }
  ]);
  const [currentPath, setCurrentPath] = useState(['Project']);
  const [editingItem, setEditingItem] = useState(null);
  const [editName, setEditName] = useState('');

  const handleEditorDidMount = (editor, monaco) => {
    editorRef.current = editor;
    setEditorMounted(true);

    // Add undo/redo handlers
    editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyCode.KeyZ, () => {
      handleUndo();
    });

    editor.addCommand(monaco.KeyMod.CtrlCmd | monaco.KeyMod.Shift | monaco.KeyCode.KeyZ, () => {
      handleRedo();
    });

    // Force initial layout after a short delay
    setTimeout(() => {
      if (editorRef.current) {
        editorRef.current.layout();
      }
    }, 100);
  };

  const handleUndo = () => {
    if (undoStack.length > 0) {
      const previousState = undoStack[undoStack.length - 1];
      setRedoStack([...redoStack, code]);
      setCode(previousState);
      setUndoStack(undoStack.slice(0, -1));
    }
  };

  const handleRedo = () => {
    if (redoStack.length > 0) {
      const nextState = redoStack[redoStack.length - 1];
      setUndoStack([...undoStack, code]);
      setCode(nextState);
      setRedoStack(redoStack.slice(0, -1));
    }
  };

  const getItemPath = (itemId) => {
    const findPath = (items, targetId, currentPath = []) => {
      for (const item of items) {
        if (item.id === targetId) {
          return [...currentPath, item.name];
        }
        if (item.children) {
          const path = findPath(item.children, targetId, [...currentPath, item.name]);
          if (path) return path;
        }
      }
      return null;
    };
    return findPath(fileTree, itemId);
  };

  const handleFileClick = (file) => {
    if (file.type === 'folder') {
      setFileTree(prevTree => {
        const updateFolder = (items) => {
          return items.map(item => {
            if (item.id === file.id) {
              return { ...item, isOpen: !item.isOpen };
            }
            if (item.children) {
              return { ...item, children: updateFolder(item.children) };
            }
            return item;
          });
        };
        return updateFolder(prevTree);
      });
    } else {
      setCurrentFile(file);
      setCode(file.content);
      setCurrentPath(getItemPath(file.id));
    }
  };

  const handleNewFile = (parentId) => {
    const newFile = {
      id: Date.now().toString(),
      name: 'untitled.py',
      type: 'file',
      content: defaultCode
    };

    setFileTree(prevTree => {
      const addFile = (items) => {
        return items.map(item => {
          if (item.id === parentId) {
            return {
              ...item,
              children: [...(item.children || []), newFile]
            };
          }
          if (item.children) {
            return { ...item, children: addFile(item.children) };
          }
          return item;
        });
      };
      return addFile(prevTree);
    });
  };

  const handleNewFolder = (parentId) => {
    const newFolder = {
      id: Date.now().toString(),
      name: 'New Folder',
      type: 'folder',
      isOpen: true,
      children: []
    };

    setFileTree(prevTree => {
      const addFolder = (items) => {
        return items.map(item => {
          if (item.id === parentId) {
            return {
              ...item,
              children: [...(item.children || []), newFolder]
            };
          }
          if (item.children) {
            return { ...item, children: addFolder(item.children) };
          }
          return item;
        });
      };
      return addFolder(prevTree);
    });
  };

  const downloadFile = (content, filename) => {
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = filename;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const handleSave = () => {
    if (currentFile) {
      // Update file content in the tree
      setFileTree(prevTree => {
        const updateFile = (items) => {
          return items.map(item => {
            if (item.id === currentFile.id) {
              return { ...item, content: code };
            }
            if (item.children) {
              return { ...item, children: updateFile(item.children) };
            }
            return item;
          });
        };
        return updateFile(prevTree);
      });

      // Update currentFile with new content
      setCurrentFile(prev => ({
        ...prev,
        content: code
      }));

      // Download the file
      downloadFile(code, currentFile.name);

      // Show save confirmation in terminal
      if (terminalInstanceRef.current) {
        terminalInstanceRef.current.write('\r\n\x1b[32mFile saved and downloaded successfully!\x1b[0m\r\n');
      }
    } else {
      // Create new file if no current file
      const newFile = {
        id: Date.now().toString(),
        name: 'untitled.py',
        type: 'file',
        content: code
      };

      setFileTree(prevTree => {
        const addFile = (items) => {
          return items.map(item => {
            if (item.id === 'root') {
              return {
                ...item,
                children: [...(item.children || []), newFile]
              };
            }
            if (item.children) {
              return { ...item, children: addFile(item.children) };
            }
            return item;
          });
        };
        return addFile(prevTree);
      });

      setCurrentFile(newFile);
      setCurrentPath(['Project', newFile.name]);

      // Download the new file
      downloadFile(code, newFile.name);

      // Show save confirmation in terminal
      if (terminalInstanceRef.current) {
        terminalInstanceRef.current.write('\r\n\x1b[32mNew file created, saved, and downloaded!\x1b[0m\r\n');
      }
    }
  };

  // Add keyboard shortcut for save (Ctrl+S)
  useEffect(() => {
    const handleKeyDown = (e) => {
      if ((e.ctrlKey || e.metaKey) && e.key === 's') {
        e.preventDefault();
        handleSave();
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
    };
  }, [code, currentFile]); // Add dependencies

  const handleThemeToggle = () => {
    setTheme(theme === 'dark' ? 'light' : 'dark');
  };

  const handleSettingsToggle = () => {
    setIsSettingsOpen(!isSettingsOpen);
  };

  const handleRename = (itemId, newName) => {
    setFileTree(prevTree => {
      const renameItem = (items) => {
        return items.map(item => {
          if (item.id === itemId) {
            return { ...item, name: newName };
          }
          if (item.children) {
            return { ...item, children: renameItem(item.children) };
          }
          return item;
        });
      };
      return renameItem(prevTree);
    });
    setEditingItem(null);
    if (currentFile && currentFile.id === itemId) {
      setCurrentPath(getItemPath(itemId));
    }
  };

  const handleDelete = (itemId) => {
    setFileTree(prevTree => {
      const deleteItem = (items) => {
        return items.filter(item => {
          if (item.id === itemId) {
            return false;
          }
          if (item.children) {
            item.children = deleteItem(item.children);
          }
          return true;
        });
      };
      return deleteItem(prevTree);
    });
    if (currentFile && currentFile.id === itemId) {
      setCurrentFile(null);
      setCode(defaultCode);
      setCurrentPath(['Project']);
    }
  };

  const startEditing = (item, e) => {
    e.stopPropagation();
    setEditingItem(item.id);
    setEditName(item.name);
  };

  const handleEditSubmit = (e) => {
    e.preventDefault();
    if (editName.trim()) {
      handleRename(editingItem, editName.trim());
    }
  };

  useLayoutEffect(() => {
    if (!terminalRef.current) return;

    // Initialize terminal with proper configuration
    const terminal = new Terminal({
      cursorBlink: true,
      theme: { background: '#000000' },
      convertEol: true,
      cursorStyle: 'block',
      disableStdin: true,
      scrollback: 10000,
      fontSize: 14,
      fontFamily: 'Consolas, monospace',
      rendererType: 'canvas',
      allowTransparency: true,
      windowsMode: true,
      cols: 80,
      rows: 24
    });

    // Initialize and configure fit addon
    const fitAddon = new FitAddon();
    terminal.loadAddon(fitAddon);
    terminal.open(terminalRef.current);

    // Store refs
    fitAddonRef.current = fitAddon;
    terminalInstanceRef.current = terminal;

    // Initial fit
    requestAnimationFrame(() => {
      fitAddon.fit();
    });

    // Handle terminal input
    terminal.onData((data) => {
      if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
        terminal.write('\r\n\x1b[31mNot connected to server\x1b[0m\r\n');
        return;
      }

      if (!isProgramRunning.current) {
        if (data === '\r') {
          terminal.clear();
          terminal.write('\r\n');
        }
        return;
      }

      switch (data) {
        case '\r': // Enter
          const input = inputBufferRef.current + '\n';
          wsRef.current.send(JSON.stringify({
            type: 'input',
            input: input
          }));
          inputBufferRef.current = '';
          terminal.write('\r\n');
          break;

        case '\x7f': // Backspace
          if (inputBufferRef.current.length > 0) {
            inputBufferRef.current = inputBufferRef.current.slice(0, -1);
            terminal.write('\b \b');
          }
          break;

        default:
          if (data >= ' ' && data <= '~') {
            inputBufferRef.current += data;
            terminal.write(data);
          }
      }
    });

    // Initialize WebSocket
    wsRef.current = new WebSocket('ws://localhost:8000/ws/terminal');

    wsRef.current.onmessage = (event) => {
      try {
        const data = event.data;
        // Write data directly to terminal
        terminal.write(data);

        if (data.includes('** Process exited')) {
          isProgramRunning.current = false;
          inputBufferRef.current = '';
          terminal.write('\r\n');
        }
      } catch (error) {
        console.error('Error writing to terminal:', error);
        terminal.write('\r\n\x1b[31mError displaying output\x1b[0m\r\n');
      }
    };

    wsRef.current.onerror = (error) => {
      console.error('WebSocket error:', error);
      terminal.write('\r\n\x1b[31mError: Could not connect to server\x1b[0m\r\n');
      isProgramRunning.current = false;
      inputBufferRef.current = '';
    };

    wsRef.current.onclose = () => {
      console.log('WebSocket connection closed');
      terminal.write('\r\n\x1b[31mConnection to server closed\x1b[0m\r\n');
      isProgramRunning.current = false;
      inputBufferRef.current = '';
    };

    return () => {
      terminal.dispose();
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  // Handle window resize
  useEffect(() => {
    const handleResize = () => {
      if (editorRef.current) {
        setTimeout(() => {
          editorRef.current.layout();
        }, 100);
      }
      if (fitAddonRef.current) {
        setTimeout(() => {
          fitAddonRef.current.fit();
        }, 100);
      }
    };

    window.addEventListener('resize', handleResize);
    handleResize(); // Initial layout

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, [editorMounted]);

  const handleRunCode = () => {
    if (!wsRef.current || wsRef.current.readyState !== WebSocket.OPEN) {
      terminalInstanceRef.current.write('\r\n\x1b[31mError: Not connected to server\x1b[0m\r\n');
      return;
    }

    terminalInstanceRef.current.clear();
    inputBufferRef.current = '';
    isProgramRunning.current = true;
    terminalInstanceRef.current.write('\r\n');

    wsRef.current.send(JSON.stringify({
      type: 'execute',
      code: code,
      args: commandLineArgs.split(' ').filter(arg => arg.length > 0)
    }));
  };

  const renderFileTree = (items, depth = 0) => {
    return items.map(item => (
      <React.Fragment key={item.id}>
        <FileTreeItem depth={depth} onClick={() => handleFileClick(item)}>
          <FileTreeContent>
            <FileTreeIcon>
              {item.type === 'folder' ? (
                item.isOpen ? <FaChevronDown /> : <FaChevronRight />
              ) : null}
            </FileTreeIcon>
            <FileTreeIcon>
              {item.type === 'folder' ? (
                item.isOpen ? <FaFolderOpen /> : <FaFolder />
              ) : (
                <FaFile />
              )}
            </FileTreeIcon>
            {editingItem === item.id ? (
              <form onSubmit={handleEditSubmit} onClick={e => e.stopPropagation()}>
                <input
                  type="text"
                  value={editName}
                  onChange={e => setEditName(e.target.value)}
                  onBlur={handleEditSubmit}
                  autoFocus
                  style={{
                    background: 'transparent',
                    border: 'none',
                    color: 'inherit',
                    outline: 'none',
                    width: '100%'
                  }}
                />
              </form>
            ) : (
              <FileTreeName>{item.name}</FileTreeName>
            )}
          </FileTreeContent>
          <FileTreeActions>
            {item.type === 'folder' ? (
              <>
                <FileTreeAction onClick={(e) => { e.stopPropagation(); handleNewFile(item.id); }}>
                  <FaPlus />
                </FileTreeAction>
                <FileTreeAction onClick={(e) => { e.stopPropagation(); handleNewFolder(item.id); }}>
                  <FaFolder />
                </FileTreeAction>
              </>
            ) : null}
            <FileTreeAction onClick={(e) => startEditing(item, e)}>
              <FaEdit />
            </FileTreeAction>
            <FileTreeAction onClick={(e) => { e.stopPropagation(); handleDelete(item.id); }}>
              <FaTrash />
            </FileTreeAction>
          </FileTreeActions>
        </FileTreeItem>
        {item.type === 'folder' && item.isOpen && item.children && (
          renderFileTree(item.children, depth + 1)
        )}
      </React.Fragment>
    ));
  };

  return (
    <ThemeProvider theme={themes[theme]}>
      <AppContainer>
        <Header>
          <LeftControls>
            <ToolbarIcon onClick={handleSave} title="Save (Ctrl+S)">
              <FaSave />
            </ToolbarIcon>
            <ToolbarIcon onClick={handleUndo}>
              <FaUndo />
            </ToolbarIcon>
            <ToolbarIcon onClick={handleRedo}>
              <FaRedo />
            </ToolbarIcon>
            <Button onClick={handleRunCode}>
              <FaPlay style={{ marginRight: '5px' }} />
              Run
            </Button>
          </LeftControls>
          <RightControls>
            <ToolbarIcon onClick={handleThemeToggle}>
              {theme === 'dark' ? <FaSun /> : <FaMoon />}
            </ToolbarIcon>
            <ToolbarIcon onClick={handleSettingsToggle}>
              <FaCog />
            </ToolbarIcon>
          </RightControls>
        </Header>

        <MainContent>
          <Sidebar>
            <FileTree>
              {renderFileTree(fileTree)}
            </FileTree>
          </Sidebar>

          <EditorWrapper>
            <FileLocation>
              {currentPath.map((item, index) => (
                <React.Fragment key={index}>
                  <LocationItem>{item}</LocationItem>
                  {index < currentPath.length - 1 && (
                    <LocationSeparator>
                      <FaChevronRightSmall size={10} />
                    </LocationSeparator>
                  )}
                </React.Fragment>
              ))}
            </FileLocation>
            <EditorContainer ref={editorContainerRef}>
              <Editor
                height="100%"
                defaultLanguage="python"
                value={code}
                onChange={newValue => {
                  setUndoStack([...undoStack, code]);
                  setCode(newValue);
                }}
                theme={theme === 'dark' ? 'vs-dark' : 'light'}
                onMount={handleEditorDidMount}
                options={{
                  minimap: { enabled: false },
                  fontSize: 14,
                  wordWrap: 'on',
                  automaticLayout: true,
                  scrollBeyondLastLine: false,
                  renderWhitespace: 'none',
                  lineNumbers: 'on',
                  roundedSelection: false,
                  scrollbar: {
                    vertical: 'visible',
                    horizontal: 'visible',
                    useShadows: false,
                    verticalScrollbarSize: 10,
                    horizontalScrollbarSize: 10
                  }
                }}
              />
            </EditorContainer>
          </EditorWrapper>

          <SettingsPanel isOpen={isSettingsOpen}>
            <h2>Settings</h2>
            <SettingsItem>
              <SettingsLabel>Font Size</SettingsLabel>
              <SettingsInput
                type="number"
                min="8"
                max="24"
                defaultValue="14"
                onChange={e => {
                  if (editorRef.current) {
                    editorRef.current.updateOptions({ fontSize: parseInt(e.target.value) });
                  }
                }}
              />
            </SettingsItem>
            <SettingsItem>
              <SettingsLabel>Tab Size</SettingsLabel>
              <SettingsInput
                type="number"
                min="2"
                max="8"
                defaultValue="4"
                onChange={e => {
                  if (editorRef.current) {
                    editorRef.current.updateOptions({ tabSize: parseInt(e.target.value) });
                  }
                }}
              />
            </SettingsItem>
          </SettingsPanel>
        </MainContent>

        <TerminalContainer ref={terminalRef} />
      </AppContainer>
    </ThemeProvider>
  );
}

export default App;
